function decrypt(src) {
	var sbox = new Array(255);
	var mykey = new Array(255);

	var key = "sdf883jsdf22";

	var a, b, c, d, e, f, g;

	// hexToChars
	a = [];
	b = (src.substr(0, 2) == '0x') ? 2 : 0;
	while (b < src.length) {
		a.push(parseInt(src.substr(b, 2), 16));
		b += 2;
	}
	var plaintxt = a; // array

	// strToChars
	a = [];
	b = 0;
	while (b < key.length) {
		a.push(key.charCodeAt(b));
		++b;
	}
	var psw = a; //

	// initialize
	a = 0;
	c = psw.length;
	d = 0;
	while (d <= 255) {
		mykey[d] = psw[d % c];
		sbox[d] = d;
		++d;
	}
	d = 0;
	while (d <= 255) {
		a = (a + sbox[d] + mykey[d]) % 256;
		b = sbox[d];
		sbox[d] = sbox[a];
		sbox[a] = b;
		++d;
	}

	// calculate
	a = 0;
	b = 0;
	c = [];
	g = 0;
	while (g < plaintxt.length) {
		a = (a + 1) % 256;
		b = (b + sbox[a]) % 256;
		e = sbox[a];
		sbox[a] = this.sbox[b];
		sbox[b] = e;
		var h = (sbox[a] + sbox[b]) % 256;
		d = sbox[h];
		f = plaintxt[g] ^ d;
		c.push(f);
		++g;
	}
	var chars = c; //

	// charsToStr
	a = "";
	b = 0;
	while (b < chars.length) {
		a += String.fromCharCode(chars[b]);
		++b;
	}
	var str = a; //

	return str;
}

