select.execute:
y.include("http://github.com/oxchronxo/YQL/raw/master/playlist.com.db.table.js");

var playlist = y.rest("http://pl.playlist.com/pl.php?playlist=" + playlist_id).get().response;
var tracklist = playlist.trackList;
var track;

for (var i = 0; i < tracklist.track.length(); i++) {
	track = tracklist.track[i];

	track.location = !track.location.split("").length ? track.originallocation : decrypt(track.location);

	delete track.trackid;
	delete track.bluewireid;
	delete track.duration;
	delete track.annotation;
	delete track.originallocation;

}

response.object = playlist;
